import { StatusBar } from 'expo-status-bar';
import { StyleSheet, Text, View } from 'react-native';
import {Picker} from '@react-native-picker/picker';
import { Button } from '@react-native-elements/base';
import { useState } from 'react';
import {BottomSheet, Card, Header, SearchBar} from 'react-native-elements';
import { Input } from '@react-native-elements/themed';
import { SafeAreaProvider } from 'react-native-safe-area-context';

export default function App() {
  const [page, setPage] = useState(0);
  const [bet, setBet] = useState('');
  const [odds, setOdds] = useState(0);
  const [number, setNumber] = useState(0);
  const [wins, setWinner] = useState(0);
  const [loss, setLoser] =  useState(0);

  const guessANumber = () => {
    setNumber(Math.floor(Math.random() * odds+1));
    console.log(number, odds);
    if(number === odds){
      setWinner(wins+1);
    } else {
      setLoser(loss+1);
    }
  };

  return (
    <View style={styles.container}>
      <SafeAreaProvider>
        <Header
          leftComponent={{icon: 'menu', color:'#fff'}}
          centerComponent={{ text: 'What Are The Odds', style: styles.heading }}
        />
      </SafeAreaProvider>
      <SafeAreaProvider>
      <StatusBar style="auto" />
      <Card style={styles.cardStyle}>
        <Text>Wins: {wins}</Text>
        <Text>Losses: {loss}</Text>
      </Card>
      <Picker
        selectedValue={odds}
        style={{height: 20, width: 100}}
        onValueChange={(itemValue, ind) => {setOdds(parseInt(itemValue))}}
      >
        <Picker.Item label='0' value={0}/>
        <Picker.Item label='1' value={1}/>
        <Picker.Item label='2' value={2}/>
        <Picker.Item label='3' value={3}/>
        <Picker.Item label='4' value={4}/>
        <Picker.Item label='5' value={5}/>
      </Picker>
      </SafeAreaProvider>
      <Button style={styles.bottomButton} onPress={() => guessANumber()} title='Click me'/>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
  cardStyle: {
    height: 100
  },
  bottomButton: {
    height: 80
  },
  headerContainer: {
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: '#397af8',
    marginBottom: 20,
    width: '100%',
    paddingVertical: 15,
  },
  heading: {
    color: 'white',
    fontSize: 22,
    fontWeight: 'bold',
  },
  headerRight: {
    display: 'flex',
    flexDirection: 'row',
    marginTop: 5,
  },
  subheaderText: {
    color: 'white',
    fontSize: 16,
    fontWeight: 'bold',
  }
});
