import { StatusBar } from 'expo-status-bar';
import { StyleSheet, Text, View, Picker } from 'react-native';
import { Button } from '@react-native-elements/base';
import { useState } from 'react';
import {BottomSheet, SearchBar} from 'react-native-elements';
import { Input } from '@react-native-elements/themed';

export default function App() {
  const [page, setPage] = useState(0);
  const [bet, setBet] = useState('');
  const [odds, setOdds] = useState(0);
  const [number, setNumber] = useState(0);

  const setOddEvent = (odd) => {
    let bet = parseInt(odd);
    setOdds(bet);
  };

  const guessANumber = () => {
    setNumber(Math.floor(Math.random() * odds+1));
    console.log(number, odds);
    if(number === odds){
      console.log('win');
    } else {
      console.log('loss');
    }
  };

  return (
    <View style={styles.container}>
      <Text>Open up App.js to start working on your app!</Text>
      <StatusBar style="auto" />
      <Input
        onChangeText={odd => setOddEvent(odd)}
      />
      <Picker
        selectedValue={odds}
        style={{height: 50, width: 150}}
        onValueChange={(itemValue, ind) => {setOdds(itemValue)}}
      >
        <Picker.Item label='0' value={0}/>
        <Picker.Item label='1' value={1}/>
        <Picker.Item label='1' value={2}/>
        <Picker.Item label='0' value={3}/>
        <Picker.Item label='1' value={4}/>
        <Picker.Item label='1' value={5}/>
      </Picker>
      <Button onPress={() => guessANumber()} title='Click me'/>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
});
