import { StatusBar } from 'expo-status-bar';
import { StyleSheet, Text, View } from 'react-native';
import {Picker, PickerIOS} from '@react-native-picker/picker';
import { Button, Overlay } from '@react-native-elements/base';
import { useState } from 'react';
import {BottomSheet, Card, Header, SearchBar} from 'react-native-elements';
import { Input } from '@react-native-elements/themed';
import { SafeAreaProvider } from 'react-native-safe-area-context';
import OddsSetter from './Views/OddsSetter';

export default function App() {
  const [page, setPage] = useState(0);
  const [bet, setBet] = useState('');
  const [betVisible, setBetVisible] = useState(false);
  const [odds, setOdds] = useState(0);
  const [number, setNumber] = useState(0);
  const [wins, setWinner] = useState(0);
  const [loss, setLoser] =  useState(0);

  const guessANumber = () => {
    setNumber(Math.floor(Math.random() * odds+1));
    console.log(number, odds);
    if(number === odds){
      setWinner(wins+1);
    } else {
      setLoser(loss+1);
    }
  };
  const toggleOverlay = () => {
    betVisible ? setBetVisible(false) : setBetVisible(true);
  }

  return (
    <View style={styles.container}>
      <SafeAreaProvider>
        <Header
          leftComponent={{icon: 'menu', color:'#fff'}}
          centerComponent={{ text: 'What Are The Odds', style: styles.heading }}
        />
        <Text style={styles.topText} h4 h4Style={{color: 'black'}}>Enter a bet and set the odds.</Text>
      <StatusBar style="auto" />
      <Card style={styles.cardStyle}>
        <Card.Title>Wins: {wins}</Card.Title>
        <Card.Title>Losses: {loss}</Card.Title>
        <Button style={styles.bottomButton} onPress={() => guessANumber()} title='Click me'/>
        <Button onPress={() => toggleOverlay()} title='Set the odds'/>
      </Card>
      <Card style={styles.cardStyle}>
        <Card.Title>Wins: {wins}</Card.Title>
        <Card.Title>Losses: {loss}</Card.Title>
        <Button style={styles.bottomButton} onPress={() => guessANumber()} title='Click me'/>
        <Button onPress={() => toggleOverlay()} title='Set the odds'/>
      </Card>
      <Overlay isVisible={betVisible} onBackdropPress={toggleOverlay}>
      <OddsSetter/>
      </Overlay>
      </SafeAreaProvider>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
  },
  cardStyle: {
  },
  bottomButton: {
    flex: 0,
    position: 'relative'
  },
  headerContainer: {
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: '#397af8',
    marginBottom: 20,
    width: '100%',
    paddingVertical: 15,
  },
  heading: {
    color: 'white',
    fontSize: 22,
    fontWeight: 'bold',
  },
  topText: {
    color: 'black',
    textAlign: 'center',
    marginTop: 15,
    marginBottom: 50,
    fontSize: 22,
    fontWeight: 'bold',
  },
  headerRight: {
    display: 'flex',
    flexDirection: 'row',
    marginTop: 5,
  },
  subheaderText: {
    color: 'white',
    fontSize: 16,
    fontWeight: 'bold',
  }
});
