import { StatusBar } from 'expo-status-bar';
import { StyleSheet, Text, View, Picker } from 'react-native';
import { Button } from '@react-native-elements/base';
import { useState } from 'react';
import {BottomSheet, Header, SearchBar} from 'react-native-elements';
import {LinearGradient} from 'react-native-linear-gradient';
import { Input } from '@react-native-elements/themed';

export default function App() {
  const [page, setPage] = useState(0);
  const [bet, setBet] = useState('');
  const [odds, setOdds] = useState(0);
  const [number, setNumber] = useState(0);
  const [wins, setWinner] = useState(0);
  const [loss, setLoser] =  useState(0);

  const guessANumber = () => {
    setNumber(Math.floor(Math.random() * odds+1));
    console.log(number, odds);
    if(number === odds){
      setWinner(wins+1);
    } else {
      setLoser(loss+1);
    }
  };

  return (
    <View style={styles.container}>
      <Header
        ViewComponent={LinearGradient}
      />
      <StatusBar style="auto" />
      <Picker
        selectedValue={odds}
        style={{height: 50, width: 150}}
        onValueChange={(itemValue, ind) => {setOdds(parseInt(itemValue))}}
      >
        <Picker.Item label='0' value={0}/>
        <Picker.Item label='1' value={1}/>
        <Picker.Item label='2' value={2}/>
        <Picker.Item label='3' value={3}/>
        <Picker.Item label='4' value={4}/>
        <Picker.Item label='5' value={5}/>
      </Picker>
      <Button onPress={() => guessANumber()} title='Click me'/>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
});
