import { View, StyleSheet } from "react-native";
import { useState } from "react";
import { Card } from "react-native-elements";
import { Text, Button } from "react-native-elements";
import axios from "axios";

const StatsPage = () => {
    const [wins, setWins] = useState(0);
    const [loss, setLoss] = useState(0);
    const getStats = () => {
       axios.get('http://localhost:8080/api/v1/user').then(res => {
           console.log(res);
           let win = res.data.wins,
            losses = res.data.loss;
            console.log(win.toString())
           setLoss(losses.toString());
           setWins(win.toString());
       }).catch(error => {
           console.log(error);
       });
    }
    return(
        <View>
        <Text style={styles.topText} h4 h4Style={{color: 'black'}}>Stats</Text>
            <Card>
                <Card.Title>Wins: {toString(wins)}</Card.Title>
                <Card.Title>Losses: {loss}</Card.Title>
                <Button onPress={getStats} title='get stats'  />
            </Card>
        </View>
    )
};
const styles = StyleSheet.create({
    topText: {
        color: 'black',
        textAlign: 'center',
        marginTop: 15,
        marginBottom: 50,
        fontSize: 22,
        fontWeight: 'bold',
    }
})
export default StatsPage;
